mapper.update:
UPDATE member SET part = #{part}
				 ,use_yn = #{use_yn}
				 ,user_pw = #{user_pw}
				 ,user_nm = #{user_nm}
				 ,user_email = #{user_email}
				 ,user_phone = #{user_phone}
				 ,user_pict = #{user_pict}
				WHERE user_id = #{user_id}
