mapper.select:
SELECT COUNT(a.stu_nm)
			FROM student a LEFT JOIN member b ON a.stu_empl = b.user_id
			WHERE 1=1
			AND a.part = #{part}
			AND a.stu_empl = #{stu_empl}

mapper.insert:
INSERT INTO student (stu_idx
				    ,part
				    ,stu_empl
				    ,stu_nm
				    ,stu_age
				    ,stu_phone
				    ,stu_email
				    ,stu_pict
				    ,use_yn)
		     VALUE(
		     	  (SELECT IFNULL(MAX(a.stu_idx), 0) + 1 FROM student AS a) 
			  	  ,#{part}
			  	  ,#{stu_empl}
			  	  ,#{stu_nm}
			  	  ,#{stu_age}
			  	  ,#{stu_phone}
			  	  ,#{stu_email}
			  	  ,#{stu_pict}
			  	  ,'Y'
			  	  )
